mapper.select:
SELECT svl.id AS id, svl.vis_type AS visType, svl.vis_time AS visTime, svl.name AS name, svl.ip AS ip, svl.browser AS browser, svl.success AS success, svl.message AS message, svl.os AS os, su.name AS userName, su.account AS account FROM sys_vis_log svl INNER JOIN sys_user su ON svl.account=su.account
AND su.account=#{account}
WHERE 1=1
AND svl.vis_type=#{visType}
AND svl.success = #{success}
AND su.id IN
#{item}
AND DATE_FORMAT(svl.vis_time,'%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
ORDER BY svl.vis_time DESC
